// client/src/components/PriceChart.js
import React, { useEffect, useState, useRef, useMemo } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, zoomPlugin);


function PriceChart({ symbol }) {
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);
  // Prevent horizontal scroll while dragging to zoom or pan
  const [isDragging, setIsDragging] = useState(false);
  const chartRef = useRef();

  useEffect(() => {
    setLoading(true);
    axios.get(`/api/prices?symbol=${symbol}&limit=1000`)
      .then(res => {
        const data = res.data;
        setChartData({
          labels: data.map(d => {
            const dt = new Date(d.timestamp);
            return dt.toLocaleDateString(undefined, { month: 'short', day: 'numeric' });
          }),
          datasets: [
            {
              label: `${symbol} Close Price`,
              data: data.map(d => d.close),
              borderColor: symbol === 'BTC/USD' ? 'orange' : 'blue',
              backgroundColor: function(ctx) {
                const chart = ctx.chart;
                const {ctx: c, chartArea} = chart;
                if (!chartArea) return null;
                const gradient = c.createLinearGradient(0, chartArea.top, 0, chartArea.bottom);
                gradient.addColorStop(0, symbol === 'BTC/USD' ? 'rgba(255,165,0,0.3)' : 'rgba(0,0,255,0.3)');
                gradient.addColorStop(1, 'rgba(0,0,0,0)');
                return gradient;
              },
              tension: 0.2,
              pointRadius: 0,
              fill: true,
            },
            {
              label: '10-Day Avg',
              data: data.map(d => d.avgPrice_10day),
              borderColor: '#888',
              backgroundColor: 'rgba(0,0,0,0)',
              borderWidth: 2,
              pointRadius: 0,
              tension: 0.2,
              spanGaps: true,
              borderDash: [6, 6],
              fill: false,
            }
          ]
        });
        setLoading(false);
      });
  }, [symbol]);

  const options = useMemo(() => ({
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: 'top' },
      title: { display: true, text: `${symbol} Price (last 1000 days)` },
      zoom: {
        zoom: {
          drag: { enabled: true },
          mode: 'x',
          onZoomStart: () => {
            setIsDragging(true);
          },
          onZoomComplete: () => {
            setIsDragging(false);
          },
        },
        pan: {
          enabled: true,
          mode: 'x',
          onPanStart: () => {
            setIsDragging(true);
          },
          onPanComplete: () => {
            setIsDragging(false);
          },
        },
        limits: {
          x: { min: 'original', max: 'original' },
          y: { min: 'original', max: 'original' }
        }
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        position: 'nearest',
        callbacks: {
          label: function(context) {
            let label = context.dataset.label || '';
            if (label) label += ': ';
            if (context.parsed.y != null) label += context.parsed.y.toLocaleString();
            return label;
          }
        }
      },
    },
    scales: {
      x: {
        display: true,
        title: { display: false },
        grid: { display: false },
        ticks: {
          maxTicksLimit: 10,
          autoSkip: true,
          maxRotation: 0,
          minRotation: 0
        }
      },
      y: {
        display: true,
        title: { display: true, text: 'Price (USD)' },
        grid: { display: true }
      }
    }
  }), [symbol]);


  if (loading) return <div>Loading chart...</div>;
  if (!chartData) return <div>No data available.</div>;

  return (
    <div>
      <button
        style={{ marginBottom: 12 }}
        onClick={() => {
          if (chartRef.current) {
            chartRef.current.resetZoom();
          }
        }}
      >
        Reset Zoom
      </button>
      <div
        style={{ width: '100%', overflowX: isDragging ? 'hidden' : 'auto' }}
      >
        <Line
          ref={chartRef}
          data={chartData}
          options={options}
          height={400}
        />
      </div>
    </div>
  );
}

export default PriceChart;
