// client/src/components/PriceChart.js
import React, { useEffect, useState, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import zoomPlugin from 'chartjs-plugin-zoom';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, zoomPlugin);


function PriceChart({ symbol }) {
  const [chartData, setChartData] = useState(null);
  const [loading, setLoading] = useState(true);
  const chartRef = useRef();

  useEffect(() => {
    setLoading(true);
    axios.get(`/api/prices?symbol=${symbol}&limit=1000`)
      .then(res => {
        const data = res.data;
        setChartData({
          labels: data.map(d => new Date(d.timestamp).toLocaleString()),
          datasets: [
            {
              label: `${symbol} Close Price`,
              data: data.map(d => d.close),
              borderColor: symbol === 'BTC/USD' ? 'orange' : 'blue',
              backgroundColor: 'rgba(0,0,0,0.05)',
              tension: 0.1,
              pointRadius: 0,
            }
          ]
        });
        setLoading(false);
      });
  }, [symbol]);

  if (loading) return <div>Loading chart...</div>;
  if (!chartData) return <div>No data available.</div>;

  return (
    <div>
      <button
        style={{ marginBottom: 12 }}
        onClick={() => {
          if (chartRef.current) {
            chartRef.current.resetZoom();
          }
        }}
      >
        Reset Zoom
      </button>
      <Line
        ref={chartRef}
        data={chartData}
        options={{
          responsive: true,
          plugins: {
            legend: { position: 'top' },
            title: { display: true, text: `${symbol} Price (last 1000 minutes)` },
            zoom: {
              zoom: {
                drag: { enabled: true },
                mode: 'x',
              },
              pan: {
                enabled: true,
                mode: 'x',
              },
              limits: {
                x: { min: 'original', max: 'original' },
                y: { min: 'original', max: 'original' }
              }
            }
          },
          scales: {
            x: { display: true, title: { display: true, text: 'Time' } },
            y: { display: true, title: { display: true, text: 'Price (USD)' } }
          }
        }}
      />
    </div>
  );
}

export default PriceChart;
